<script>
import Header from '@/views/user/Header'
import Footer from '@/views/user/Footer'

export default {
  name: "Pay",
  components: {
    "v-header": Header,
    "v-footer": Footer
  },
  data() {
    return {
      payBtnText: '立即支付',
      isChosen: false,
      alipay: false,
      wechat: false
    };
  },
  methods: {
    useAlipay: function() {
      if (!this.isChosen) {
        this.alipay = true
        this.isChosen = true
      } else if (this.alipay && !this.wechat) {
        this.alipay = false
        this.isChosen = false
      } else if (!this.alipay && this.wechat) {
        this.alipay = true
        this.wechat = false
      }
    },
    useWechat: function() {
      if (!this.isChosen) {
        this.wechat = true
        this.isChosen = true
      } else if (this.alipay && !this.wechat) {
        this.alipay = false
        this.wechat = true
      } else if (!this.alipay && this.wechat) {
        this.wechat = false
        this.isChosen = false
      }
    },
    payNow: function() {
      this.payBtnText = '支付中...'
      this.isChosen = false
      this.$message
          .loading('支付中..', 1)
          .then(() => {
            this.$message.success('支付成功', 2.5)
            this.payBtnText = '立即支付'
            this.alipay = false
            this.wechat = false
          })
    }
  }
};
</script>
